import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './upload_image.css';

function ImageUploader() {
  const [selectedImage, setSelectedImage] = useState(null);
  const [isPageOpen, setIsPageOpen] = useState(false);

  useEffect(() => {
    const checkPageStatus = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/ping'); // Cambia la URL según tu configuración de servidor
        if (response.status === 200) {
          setIsPageOpen(true);
        }
      } catch (error) {
        console.error('Error al verificar el estado de la página:', error);
      }
    };

    const interval = setInterval(checkPageStatus, 5000); // Verifica el estado de la página cada 5 segundos

    return () => {
      clearInterval(interval); // Limpia el intervalo cuando el componente se desmonta
    };
  }, []);

  const handleImageChange = (event) => {
    setSelectedImage(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (!selectedImage) {
      console.error('No se ha seleccionado ninguna imagen.');
      return;
    }

    const formData = new FormData();
    formData.append('image', selectedImage);

    try {
      await axios.post('http://localhost:8000/api/image', formData);
      console.log('Imagen enviada correctamente.');
    } catch (error) {
      console.error('Error al enviar la imagen:', error);
    }
  };

  return (
    <div>
      {isPageOpen ? (
        <form onSubmit={handleSubmit} className="initial_form">
          <label htmlFor="file_upload" className="custom-file-upload">
            <span>Seleccionar archivo</span>
          </label>
          <input
            id="file_upload"
            hidden
            type="file"
            accept="image/*"
            name="imagen"
            className="upload_image"
            onChange={handleImageChange}
          />
          <button type="submit" className="primary_button">
            Submit
          </button>
        </form>
      ) : (
        <p>La página no está abierta.</p>
      )}
    </div>
  );
}

export default ImageUploader;
